mapper.select:
SELECT * 
FROM db_cars.car_infors
where 1 = 1 



    and YEAR > #{words}



    and CAR_NAME like CONCAT(#{words}, '%')


;


SELECT * 
FROM db_cars.car_infors
where 1 = 1
and CAR_INFOR_ID in 

    #{CAR_INFOR_ID}

;

mapper.update:
update car_infors
set CAR_NAME = #{CAR_NAME}
where CAR_INFOR_ID = #{CAR_INFOR_ID}
