mapper.update:
update commons_code
set NAME = #{NAME}, SYSTEM_CODE_YN = #{SYSTEM_CODE_YN}, MODIFIER_SEQ = #{MODIFIER_SEQ}, MODIFY_DATE = now()
where COMMON_CODE_ID = #{COMMON_CODE_ID}

mapper.select:

SELECT * 
FROM commons_code
where 1 = 1
and COMMON_CODE_ID in 

    #{COMMON_CODE_ID}

;

SELECT *
FROM commons_code
where 1 = 1 


    and NAME like CONCAT(#{words}, '%')


LIMIT #{pageBegin}, #{pageScale}
;

SELECT count(*) as cnt
FROM commons_code
;
